import API from "./axiosInstance"; // import the axios instance with interceptor

;

export const createStatement = (data) => API.post("/statements", data).then(res => res.data);
export const updateStatement = (id, data) => API.put(`/statements/${id}`, data).then(res => res.data);
export const deleteStatement = (id) => API.delete(`/statements/${id}`).then(res => res.data);
