import axios from "axios";

const API_URL = "http://localhost:5000/api/statements";

const getAuthHeaders = () => {
  const token = localStorage.getItem("token");
  if (!token) throw new Error("Unauthorized: No token found");
  return {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };
};

export const fetchStatements = async () => {
  const response = await axios.get(API_URL, getAuthHeaders());
  return response.data;
};

export const createStatement = async (statementData) => {
  return await axios.post(API_URL, statementData, getAuthHeaders());
};

export const updateStatement = async (id, updatedData) => {
  const response = await axios.put(`${API_URL}/${id}`, updatedData, getAuthHeaders());
  return response.data;
};

export const deleteStatement = async (id) => {
  const response = await axios.delete(`${API_URL}/${id}`, getAuthHeaders());
  return response.data;
};
