import axios from "axios";

const API = axios.create({ baseURL: "http://localhost:5000/api" });

// ✅ Get all statements for the authenticated user
export const getStatements = async () => {
  try {
    const response = await axios.get(`${API_URL}/statements`, authHeaders());
    return response.data;
  } catch (error) {
    console.error("Error fetching statements:", error.response?.data || error);
    throw error;
  }
};

// ✅ Create a new statement
export const createStatement = async (data) => {
  try {
    const response = await axios.post(`${API_URL}/statements`, data, authHeaders());
    return response.data;
  } catch (error) {
    console.error("Error creating statement:", error.response?.data || error);
    throw error;
  }
};

// ✅ Update a statement
export const updateStatement = async (id, data) => {
  try {
    const response = await axios.put(`${API_URL}/statements/${id}`, data, authHeaders());
    return response.data;
  } catch (error) {
    console.error("Error updating statement:", error.response?.data || error);
    throw error;
  }
};

// ✅ Delete a statement
export const deleteStatement = async (id) => {
  try {
    const response = await axios.delete(`${API_URL}/statements/${id}`, authHeaders());
    return response.data;
  } catch (error) {
    console.error("Error deleting statement:", error.response?.data || error);
    throw error;
  }
};
