import { Link } from "react-router-dom";
import React, { useState, useEffect } from "react";
import "../CSS/homepage.css";
import { useFinance } from "../context/FinanceContext";

function Homepage() {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [pressTimer, setPressTimer] = useState(null);
    const [goals, setGoals] = useState({ shortTerm: [], longTerm: [] });
    const [user, setUser] = useState(null);
    const [statements, setStatements] = useState([]);

    useEffect(() => {
        const userData = JSON.parse(localStorage.getItem('user'));
        if (userData) {
            setUser(userData);
        }
    }, []);

   
    // Save to localStorage whenever goals change
    useEffect(() => {
        if (goals.shortTerm.length > 0 || goals.longTerm.length > 0) {
            localStorage.setItem('goals', JSON.stringify(goals));
        }
    }, [goals]);

    // Save to localStorage whenever statements change
    useEffect(() => {
        if (statements.length > 0) {
            localStorage.setItem('statements', JSON.stringify(statements));
        }
    }, [statements]);

    // Fetch goals and statements from localStorage
    useEffect(() => {
        const storedGoals = JSON.parse(localStorage.getItem('goals'));
        if (storedGoals) {
            setGoals(storedGoals);
        }

        const storedStatements = JSON.parse(localStorage.getItem('statements'));
        if (storedStatements) {
            setStatements(storedStatements);
        }
    }, []);

    const handlePressStart = () => {
        const timer = setTimeout(() => {
            setIsModalOpen(true);
        }, 400);
        setPressTimer(timer);
    };

    const handlePressEnd = () => {
        clearTimeout(pressTimer);
    };

    const handleCloseModal = (e) => {
        if (e.target.className === "modal-overlay") {
            setIsModalOpen(false);
        }
    };

    return (
        <div className="color1">
            <div className={`title fontstyle1 ${isModalOpen ? "blur-background" : ""}`}>
                <h1 className="head1">BILL <br /> POCKET</h1>
                <p>Create your goals.. Track your expense...</p>
            </div>
            <div className={`conwrp ${isModalOpen ? "blur-background" : ""}`}>
                <div className="bnkdtls">
                    <Link to="/page1" className="container1">
                        <p className="txt">
                            {user ? (
                                <>
                                    Name: {user.name}<br />
                                    Bank 2 IFSC: {user.ifsc}<br />
                                    Bank Balance: {user.balance}/-
                                </>
                            ) : (
                                <>User data not found</>
                            )}
                        </p>
                    </Link>
                    <Link to="/page1" className="container1">
                        <p className="txt">
                            Available balance:100000/-<br />
                            Goal Expence:--<br />
                            Expected Expenditure:--<br />
                        </p>
                    </Link>
                </div>
                <Link to="/goal" className="container2">
                    <h1 className="head2">Goals</h1>
                    <p className="txt">
                        {goals.shortTerm.length === 0 && goals.longTerm.length === 0 ? (
                            <span className="empty-message">No goals set yet. Click to add your first goal!</span>
                        ) : (
                            <>
                                {goals.shortTerm.slice(0, 2).map((goal, index) => (
                                    <React.Fragment key={`short-${index}`}>
                                        {goal.date} - {goal.goal} - ${goal.cost}<br />
                                    </React.Fragment>
                                ))}
                                {goals.longTerm.slice(0, 1).map((goal, index) => (
                                    <React.Fragment key={`long-${index}`}>
                                        {goal.date} - {goal.goal} - ${goal.cost}<br />
                                    </React.Fragment>
                                ))}
                            </>
                        )}
                    </p>
                </Link>
                <Link to="/statement" className="container3">
                    <h1 className="head2">Statements</h1>
                    <div onMouseDown={handlePressStart} onMouseUp={handlePressEnd} onMouseLeave={handlePressEnd}>
                        <p className="txt">
                            {statements.length === 0 ? (
                                <span>No statements available.</span>
                            ) : (
                                statements.slice(0, 1).map((statement, i) => (
                                    <span key={i}>
                                        {statement.date} - {statement.item} - ${statement.cost}<br />
                                        console.log(statement.date, statement.item, statement.cost)<br />
                                    </span>
                                ))
                            )}
                        </p>
                    </div>
                </Link>
            </div>
            {isModalOpen && (
                <div className="modal-overlay" onClick={handleCloseModal}>
                    <div className="modal-content">
                        <h2>Transaction Details</h2>
                        <p>
                            2025-04-10 – Apple Store – MacBook Pro Purchase – $2,499.00<br />
                            2025-04-05 – Airbnb – Tokyo Booking – $1,150.00<br />
                            2025-03-03 – Delta Airlines – Japan Flight – $850.00<br />
                            2025-03-30 – Credit Card Payment – Chase – $3,200.00<br />
                            2025-03-25 – Coursera – UX Design Course – $749.00<br />
                        </p>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Homepage;
