import axios from "axios";

const API = axios.create({ baseURL: "http://localhost:5000/api" });

API.interceptors.request.use((req) => {
  const token = localStorage.getItem("token");
  if (token) {
    req.headers.Authorization = `Bearer ${token}`;
  }
  return req;
});

// Example: Get Statements
export const fetchStatements = async () => {
  const response = await API.get("/statements");
  return response.data;
};

// Add other CRUD functions here...
export const createStatement = async (data) => {
  const response = await API.post("/statements", data);
  return response.data;
};

export const updateStatement = async (id, data) => {
  const response = await API.put(`/statements/${id}`, data);
  return response.data;
};

export const deleteStatement = async (id) => {
  const response = await API.delete(`/statements/${id}`);
  return response.data;
};
