import axiosInstance from "./axiosInstance"; // ✅ use secure axios

export const fetchStatements = async () => {
  const response = await axiosInstance.get("/statements");
  return response.data;
};

export const createStatement = async (statementData) => {
  const response = await axiosInstance.post("/statements", statementData);
  return response.data;
};

export const updateStatement = async (id, statementData) => {
  const response = await axiosInstance.put(`/statements/${id}`, statementData);
  return response.data;
};

export const deleteStatement = async (id) => {
  const response = await axiosInstance.delete(`/statements/${id}`);
  return response.data;
};
