import React, { useState, useEffect } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
} from "recharts";
import "../CSS/statement.css";
import {
  fetchStatements,
  createStatement,
  updateStatement,
  deleteStatement,
} from "../api";

function Statement() {
  const [statements, setStatements] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newStatement, setNewStatement] = useState({ date: "", item: "", cost: "", note: "" });
  const [editIndex, setEditIndex] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortKey, setSortKey] = useState("date");
  const monthlyBudget = 10000; // Set your monthly budget limit

  useEffect(() => {
    const getStatements = async () => {
      try {
        const user = JSON.parse(localStorage.getItem("user"));
        if (!user || !user._id) return;
        const res = await fetchStatements(user._id);
        setStatements(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    getStatements();
  }, []);

  const calculateTotalExpense = () =>
    statements.reduce((total, s) => total + parseFloat(s.cost || 0), 0).toFixed(2);

  const currentMonth = new Date().getMonth();
  const monthlyExpenses = statements
    .filter((s) => new Date(s.date).getMonth() === currentMonth)
    .reduce((total, s) => total + parseFloat(s.cost || 0), 0);

  // ðŸ”” Monthly Budget Alert
  useEffect(() => {
    if (monthlyExpenses > monthlyBudget) {
      alert("âš ï¸ Youâ€™ve exceeded your monthly budget!");
    } else if (monthlyExpenses > monthlyBudget * 0.9) {
      alert("ðŸ”” Youâ€™re close to your monthly budget.");
    }
  }, [monthlyExpenses]);

  const lineChartData = [...statements]
    .map((s) => ({
      date: s.date,
      cost: parseFloat(s.cost),
    }))
    .sort((a, b) => new Date(a.date) - new Date(b.date));

  
  const handleEditStatement = (index) => {
    setNewStatement(statements[index]);
    setEditIndex(index);
    setIsModalOpen(true);
  };

  const handleDeleteStatement = async (index) => {
    try {
      await deleteStatement(statements[index]._id);
      setStatements(statements.filter((_, i) => i !== index));
    } catch (err) {
      console.error(err);
    }
  };

  const handleCloseModal = () => {
    setNewStatement({ date: "", item: "", cost: "", note: "" });
    setIsModalOpen(false);
    setEditIndex(null);
  };

  const filteredStatements = [...statements]
    .filter((s) => s.item.toLowerCase().includes(searchTerm.toLowerCase()))
    .sort((a, b) => {
      if (sortKey === "cost") return parseFloat(b.cost) - parseFloat(a.cost);
      if (sortKey === "date") return new Date(b.date) - new Date(a.date);
      return 0;
    });

  return (
    <div className="statement-page">
      <div className="column">
        <div className="column-header">
          <h2>Statements</h2>
          <button onClick={() => setIsModalOpen(true)}>Add</button>
        </div>

        {/* Filter & Sort Controls */}
        <div className="controls">
          <input
            type="text"
            placeholder="Search by item..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <select value={sortKey} onChange={(e) => setSortKey(e.target.value)}>
            <option value="date">Sort by Date</option>
            <option value="cost">Sort by Cost</option>
          </select>
        </div>

        <ul>
          {filteredStatements.map((statement, index) => (
            <li key={index}>
              <span><strong>Date:</strong> {statement.date}</span>
              <span><strong>Goal:</strong> {statement.item}</span>
              <span><strong>Cost:</strong> â‚¹{statement.cost}</span>
              {statement.note && <span><strong>Note:</strong> {statement.note}</span>}
              <div className="statement1-actions">
                <button onClick={() => handleEditStatement(index)}>Edit</button>
                <button onClick={() => handleDeleteStatement(index)}>Delete</button>
              </div>
            </li>
          ))}
        </ul>

        <div className="expensse-slot">
          <span>Total Expense: â‚¹{calculateTotalExpense()}</span>
          <span style={{ color: monthlyExpenses > monthlyBudget ? "red" : "green" }}>
            This Month: â‚¹{monthlyExpenses} / â‚¹{monthlyBudget}
          </span>
        </div>

        <div className="chart-container">
          <h3>Expense Trend</h3>
          {lineChartData.length > 0 ? (
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={lineChartData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis />
                <Tooltip />
                <Line type="monotone" dataKey="cost" stroke="#82ca9d" strokeWidth={2} />
              </LineChart>
            </ResponsiveContainer>
          ) : (
            <p>No data to display</p>
          )}
        </div>
      </div>

      {/* Modal for Add/Edit */}
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h3>{editIndex !== null ? "Edit Statement" : "Add Statement"}</h3>
            <input
              type="date"
              value={newStatement.date}
              onChange={(e) => setNewStatement({ ...newStatement, date: e.target.value })}
              placeholder="Date"
            />
            <input
              type="text"
              value={newStatement.item}
              onChange={(e) => setNewStatement({ ...newStatement, item: e.target.value })}
              placeholder="Item"
            />
            <input
              type="number"
              value={newStatement.cost}
              onChange={(e) => setNewStatement({ ...newStatement, cost: e.target.value })}
              placeholder="Cost"
            />
            <textarea
              value={newStatement.note}
              onChange={(e) => setNewStatement({ ...newStatement, note: e.target.value })}
              placeholder="Optional Note"
            />
            <div className="modal-buttons">
              <button onClick={handleAddStatement}>Submit</button>
              <button onClick={handleCloseModal}>Close</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Statement;
