import axios from 'axios';

const API = axios.create({ baseURL: 'http://localhost:5000/api' });

// Add auth token to requests
API.interceptors.request.use((req) => {
  if (localStorage.getItem('token')) {
    req.headers.Authorization = `Bearer ${localStorage.getItem('token')}`;
  }
  else {
    console.warn("No authentication token found."); // Debugging log
  }

  return req;
});

// Auth API
export const register = (userData) => API.post('/auth/register', userData);
export const login = (userData) => API.post('/auth/login', userData);

// Goals API
export const fetchGoals = () => API.get('/goals');
export const createGoal = (newGoal) => API.post('/goals', newGoal);
export const updateGoal = (id, updatedGoal) => API.put(`/goals/${id}`, updatedGoal);
export const deleteGoal = (id) => API.delete(`/goals/${id}`);

// Statements API
export const fetchStatements = () => API.get('/statements');
export const createStatement = (newStatement) => API.post('/statements', newStatement);
export const updateStatement = (id, updatedStatement) => API.put(`/statements/${id}`, updatedStatement);
export const deleteStatement = (id) => API.delete(`/statements/${id}`);
