import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../CSS/login.css";

function Login({ setIsLoggedIn }) {
    const [isFlipped, setIsFlipped] = useState(true); // Default to true to show the registration page first
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const navigate = useNavigate();

    const handleFlip = () => {
        setIsFlipped(!isFlipped);
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        const formData = new FormData(e.target);
        const data = Object.fromEntries(formData.entries());
        
        // Add client-side validation
        if (data.password !== data['confirm-password']) {
            setError("Passwords don't match");
            return;
        }
        
        try {
            const response = await fetch('http://localhost:5000/api/auth/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    username: data.username,
                    name: data.name,
                    email: data.email,
                    mobile: data.mobile,
                    ifsc: data.ifsc,
                    balance: parseFloat(data.balance),
                    password: data.password,
                    confirmPassword: data['confirm-password']
                }),
            });
            
            const result = await response.json();
            
            if (!response.ok) {
                throw new Error(result.message || 'Registration failed');
            }
            
            if (result.success) {
                alert("Registration successful!");
                setIsFlipped(false); // Flip to login page
                setError(""); // Clear any previous errors
            } else {
                setError(result.message || "Registration failed");
            }
        } catch (err) {
            console.error('Registration error:', err);
            setError(err.message || "Registration failed. Please try again.");
        }
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        const formData = new FormData(e.target);
        const data = Object.fromEntries(formData.entries());
        try {
            const response = await fetch('http://localhost:5000/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({  username: data.usernamel, password: data.passwordl }),
            });
            
            const result = await response.json();
            console.log(result);
            if (result.success) {
                localStorage.setItem('user', JSON.stringify(result.user)); // Store user data
               //setIsLoggedIn(true);
               console.log("yes");
                navigate("/profile");
            } else {
                setError(result.message || "Invalid credentials");
            }
        } catch (err) {
            setError("Login failed. Please try again.");
        }
    };

    return (
        <div className={`content ${isFlipped ? "flipped-background" : ""}`}>
            <div className={`login-container ${isFlipped ? "flipped" : ""}`}>
                {/* Registration Form */}
                <div className="registration-form-container">
                    <h2 className="login-heading">Register</h2>
                    <form className="registration-form" onSubmit={handleRegister}>
                        {/* Left Column */}
                        <div className="registration-column">
                            <div className="form-group">
                                <label htmlFor="username">Username</label>
                                <input
                                    type="text"
                                    id="username"
                                    name="username"
                                    placeholder="Enter your username"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="email">Email</label>
                                <input
                                    type="email"
                                    id="email"
                                    name="email"
                                    placeholder="Enter your email"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="balance">Bank Balance</label>
                                <input
                                    type="number"
                                    id="balance"
                                    name="balance"
                                    placeholder="Enter your bank balance"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="password">Password</label>
                                <input
                                    type="password"
                                    id="password"
                                    name="password"
                                    placeholder="Enter your password"
                                    required
                                />
                            </div>
                        </div>

                        {/* Right Column */}
                        <div className="registration-column">
                            <div className="form-group">
                                <label htmlFor="name">Name</label>
                                <input
                                    type="text"
                                    id="name"
                                    name="name"
                                    placeholder="Enter your name"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="mobile">Mobile Number</label>
                                <input
                                    type="text"
                                    id="mobile"
                                    name="mobile"
                                    placeholder="Enter your mobile number"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="ifsc">IFSC</label>
                                <input
                                    type="text"
                                    id="ifsc"
                                    name="ifsc"
                                    placeholder="Enter your bank IFSC code"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="confirm-password">Confirm Password</label>
                                <input
                                    type="password"
                                    id="confirm-password"
                                    name="confirm-password"
                                    placeholder="Confirm your password"
                                    required
                                />
                            </div>
                        </div>
                    </form>
                    <div className="form-actions">
                            <button type="submit" className="login-button">
                                Register
                            </button>
                            <p className="toggle-text" onClick={handleFlip}>
                                Already have an account? Login here
                            </p>
                        </div>
                    
                </div>

                {/* Login Form */}
                <div className="login-form-container">
                    <h2 className="login-heading">Login</h2>
                    {error && <p className="error">{error}</p>}
                    <form className="login-form" onSubmit={handleLogin}>
                        <div className="form-group">
                            <label htmlFor="username">Username</label>
                            <input
                                type="text"
                                id="usernamel"
                                name="usernamel"
                                placeholder="Enter your username"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <input
                                type="password"
                                id="passwordl"
                                name="passwordl"
                                placeholder="Enter your password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        <button type="submit" className="login-button">
                            Login
                        </button>
                        <p className="toggle-text" onClick={handleFlip}>
                            Don't have an account? Register here
                        </p>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Login;
